import React, { Component } from 'react'

export default class NewsItem extends Component {

  render() {
    let { title, description, imageUrl, newsUrl,author,date,source } = this.props;
    return (
      <div className='my-3'>
        <div className="card">
          <img src={imageUrl} className="card-img-top" alt="..." />
          <div className="card-body">

            <h5 className="card-title">{title}...</h5>
            <span class="badge rounded-pill text-bg-danger" onMouseOver={{backgroundColor : "blue"}}>{source}</span>

            <p className="card-text">{description}...</p>
            <p class="card-text"><small class="text-muted">By {!author?"Unknown":author} on {new Date(date).toUTCString()}</small></p>
            <a rel="noreferrer" href={newsUrl} target="_blank" className="btn btn-dark btn-sm btn-primary">Read more</a>


          </div>
        </div>
      </div>
    )
  }
}
