import React, { Component } from 'react'
import loading from "./loading.gif";

export  class Spinner extends Component {
  render() {
    return (
      <div className='text-center'>
        <p>Loading...</p>
        <img src = {loading} alt = "spinner"></img>
      </div>
    )
  }
}

export default Spinner
