import React, { Component } from 'react'
import NewsItem from './NewsItem'

export default class News extends Component {


    constructor() {
        super();
        this.state = {
            articles: [],
            loading: false,
            page : 1
        }

    }


    async componentDidMount() {
        let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=1dd6ba6dea624ee08026869c79c64612&page=1&pageSize=20";
        let data = await fetch(url);
        let parsedData = await data.json();
        this.setState({ articles: parsedData.articles,totalResults : parsedData.totalResults})
    }

    handleNextClick = async() => {
        if(Math.ceil(this.state.totalResults/20) < this.state.page + 1){
        }
        else{
            let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=1dd6ba6dea624ee08026869c79c64612&page=${this.state.page+1}&pageSize=20`;
            let data = await fetch(url);
            let parsedData = await data.json();
            this.setState({ 
                articles: parsedData.articles,
                page : this.state.page + 1 
            })
        }
    }

    handlePreviousClick = async() => {
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=1dd6ba6dea624ee08026869c79c64612&page=${this.state.page-1}&pageSize=20`;
        let data = await fetch(url);
        let parsedData = await data.json();
        this.setState({ 
            articles: parsedData.articles,
            page : this.state.page - 1 

        })
    }


    render() {
        return (
            <div className='container my-3'>
                <h1 style={{ "textAlign": 'center', "color": "blue", "fontFamily": "Roboto" }}><strong><u>NewsMonkey - Top Headlines</u></strong></h1>
                <div className="row my-4">
                    {this.state.articles.map((element) => {
                        return <div className="col-md-4" key={element.url}>
                            <NewsItem title={element.title} description={element.description} imageUrl={element.urlToImage?"https://play-lh.googleusercontent.com/uAu69C99ameBLdlE1Iv2ypibSqwQ3lqQGqm0NzcR1XpQ5gjBfNfW2sotbhfg2hPSjGZ3" : element.urlToImage} newsUrl={element.url} />
                        </div>
                    })}
                </div>

                <div className='container d-flex justify-content-between'>

                    <button disabled = {this.state.page <= 1} type="button" className="btn btn-dark" onClick={this.handlePreviousClick}>&larr; Previous</button>
                    <button type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
                </div>
            </div>
        )
    }
}
